childDataGroups.groupGroovyScript:
przyczynaZaprzestaniaKod = u.getComponentValue("przyczynaZaprzestaniaKod");

if(przyczynaZaprzestaniaKod == "9") {
 u.setComponentRequired(true, "przyczynaZaprzestaniaWyjasnienie");
} else {
 u.setComponentRequired(false, "przyczynaZaprzestaniaWyjasnienie");
}
return [true, ""];

dataItem.observerDefinition:
val = u.getComponentValue("rodzajKod");
                        if(val == "3") {
                         u.setVisibleByProperties(true, "adresEPUAP", "email", "telefonSMS", "telefon");
                          u.setComponentRequired(true, "email", "telefon");

                        } else {
                          u.setVisibleByProperties(false, "adresEPUAP", "email", "telefonSMS", "telefon");
                          u.setComponentRequired(false, "email", "telefon");

                        }
                        u.clearValueByPropertiesName("adresEPUAP", "email", "telefonSMS", "telefon");
                        u.reloadForm("rodzajKod");

value = u.getComponentValue("rodzajKod");
if(value != null) {
    if(value == "3") {
       u.setEnabledByProperty(true, "telefon", "telefonSMS", "email", "adresEPUAP");
     } else if (value == "18") {
                            u.setEnabledForGroupGlobal(false, "Adres");
                            } else if (value != "3" && value != "18" ) {
                            u.setEnabledByProperty(false, "telefon", "telefonSMS", "email", "adresEPUAP");
                            } else {
                            u.setEnabledForGroupGlobal(true, "Adres");
                            }
                            }

value = u.getComponentValue("krajKod");
address = u.getComponentValue("rodzajKod");
    if(value =="PL"){
        u.setVisibleByProperties(true, "wojewodztwoKod", "powiatKod", "gminaKod", "miejscowoscKod",  "ulicaKod");
        u.setVisibleByProperties(false, "miejscowoscNazwa",  "ulicaNazwa");
        u.setEnabledByProperty(true, "wojewodztwoKod");
        u.setEnabledByProperty(false, "miejscowoscKod",  "ulicaKod");
        u.setComponentRequired(true, "wojewodztwoKod", "powiatKod", "gminaKod",  "miejscowoscKod");
        u.setComponentRequired(false, "miejscowoscNazwa");
        u.allowCustomValue("kodPocztowy", false);
    }else{
        u.setVisibleByProperties(false, "wojewodztwoKod", "powiatKod", "gminaKod", "miejscowoscKod",  "ulicaKod");
        u.setVisibleByProperties(true, "miejscowoscNazwa",  "ulicaNazwa");
        u.setEnabledByProperty(true, "miejscowoscNazwa", "ulicaNazwa");
        u.setEnabledByProperty(false, "miejscowoscKod", "ulicaKod");
        u.setComponentRequired(false, "wojewodztwoKod", "powiatKod", "gminaKod", "miejscowoscKod");
        u.setComponentRequired(true, "miejscowoscNazwa",  "ulicaNazwa");
        u.allowCustomValue("kodPocztowy", true);
       }
u.clearValueByPropertiesName("wojewodztwoNazwa","powiatNazwa","gminaNazwa","miejscowoscNazwa","ulicaNazwa","nrDomu", "nrLokalu", "kodPocztowy");
 u.clearValueByPropertiesName("wojewodztwoKod", "powiatKod", "gminaKod", "miejscowoscKod", "ulicaKod");
u.reloadForm("krajKod");

