dataItem.groovyValidator:
regon = u.getComponentValue("regon");
krs = u.getComponentValue("nrKrs");
compVal = u.getComponentValue("nip");

if(u.checkStringValueisEmpty(regon) && u.checkStringValueisEmpty(krs) &&
     u.checkStringValueisEmpty(compVal)) {
      return [false,"Jedno z pól Regon, KRS lub NIP jest wymagane"];
} else if (!u.validateNip(compVal)) {
      return [false,"Niepoprawny numer NIP"];
} else {
      return[true, ""];
}

dataItem.observerDefinition:
compVal = u.getComponentValue("nip");
if (!u.validateNip(compVal)) {
u.setInvalidForField("nip",  "Niepoprawny numer NIP");
}

