dataItem.observerDefinition:
value = u.getComponentValue("rodzajOsoby");
                        if(value !=null && value == "PG") {
u.setVisibleByProperties(false, "imie", "nazwisko", "pesel",  "nrIdentyfikacyjny", "obywatelstwoKod");
                            u.setVisibleByProperties(true,  "nazwaPelna",  "nazwaSkrocona", "nip", "regon", "nrVatUe", "nrTc", "eori");
                        } else if (value !=null && value == "OF") {
u.setVisibleByProperties(true, "imie", "nazwisko", "pesel",  "nrIdentyfikacyjny", "obywatelstwoKod", "eori");
                            u.setVisibleByProperties(false,  "nazwaPelna",  "nazwaSkrocona", "nip", "regon", "nrVatUe", "nrTc");
} else {
