                                   }
                        u.reloadForm("rodzajOsoby");
                        u.clearValueByPropertiesName("imie", "nazwisko", "pesel",  "nrIdentyfikacyjny", "obywatelstwoKod", "idsiscop",  "nazwaPelna",  "nazwaSkrocona", "nip", "regon", "nrVatUe", "nrTc", "eori");

dataItem.groovyValidator:
