  return[true, ""];
} else {
  return[false, "Niepoprawny numer NIP"];
}

if (u.validateRegon(v)) {
  return[true, ""];
} else {
  return[false, "Niepoprawny numer REGON"];
}

if(u.validatePesel(v)) {
   return[true, ""];
} else {
   return[false, "Nieprawidłowy numer PESEL"];
}

if (v != null && v != "") {
   isValid = u.validateVatUE(v);
   if (isValid) {
      return [true, ""];
   } else {
      return [false, "Nieprawidłowy numer VAT UE. Przykładowa wartość to PL9999999999"];
   }
} else {
   return [true, ""];
}

